INSERT INTO `main_kpi` VALUES (41, '04800', 'ร้อยละของโรงพยาบาลศูนย์ผ่านเกณฑ์ ER คุณภาพ', '', NULL, '80', 'ค่าเป้าหมาย ไม่ต่ำกว่าร้อยละ 80', '>=', 'ร้อยละ', '(A/B)*100', '', 'A = จํานวนโรงพยาบาล ระดับ A ที่ผ่านเกณฑ์ประเมิน ER คุณภาพ', 'B = จํานวนโรงพยาบาล ระดับ A ทั้งหมด ในสังกัดสํานักงานปลัดกระทรวงสาธารณสุข', 12);
INSERT INTO `main_kpi` VALUES (42, '04900', 'จํานวนผู้ป่วยที่ไม่ฉุกเฉินในห้องฉุกเฉินระดับ 4 และ 5 (Non trauma) ลดลง', '', NULL, '5', 'ค่าเป้าหมาย ลดลงมากกว่าหรือเท่ากับ ร้อยละ 5', '>=', 'ร้อยละ', '(A-B)*100/A', '', 'A = จํานวนผู้ป่วยที่ไม่ฉุกเฉินในห้องฉุกเฉินระดับ 4 และ 5 (Non trauma) ปีงบประมาณ 2562', 'B = จํานวนผู้ป่วยที่ไม่ฉุกเฉินในห้องฉุกเฉินระดับ 4 และ 5 (Non trauma) ปีงบประมาณ 2563', 12);
